#!/usr/bin/ruby

#modulo = ''

File.open(ARGV[0]) do |f1|
  f1.each_line do |sslmodulus|
    sslmodulus.chomp!
    arr = sslmodulus.split(":")
    File.open(ARGV[1]) do |f|
      f.each_line do |prime|
        prime.chomp!
        amari = (arr[1].hex % prime.hex)
        if amari == 0 then
          puts "HOST[#{arr[0]}] PRIME[#{prime}]:AMARI[#{amari}]"
        end
      end
    end
  end
end

